document.addEventListener("DOMContentLoaded",function(){const header=document.querySelector(".header");const btnToggle=document.querySelector(".button-mobile__toggle");const navLinksContainer=document.querySelector(".header-navbar__links");const stickyButtons=document.querySelector(".sticky-buttons");const allSections=document.querySelectorAll(".section");if(btnToggle&&header){btnToggle.addEventListener("click",function(){header.classList.toggle("nav-open");const isOpen=header.classList.contains("nav-open");btnToggle.setAttribute("aria-expanded",isOpen)})}if(navLinksContainer&&header){navLinksContainer.addEventListener("click",function(e){const target=e.target.closest("a");if(!target||window.innerWidth>768)return;if(header.classList.contains("nav-open")){header.classList.remove("nav-open");btnToggle?.setAttribute("aria-expanded","false")}})}if(navLinksContainer){navLinksContainer.addEventListener("click",function(e){const target=e.target.closest("a");if(!target||!target.hash||target.hash==="#")return;e.preventDefault();const hash=target.hash;const targetElement=document.querySelector(hash);if(targetElement){setTimeout(()=>{targetElement.scrollIntoView({behavior:"smooth",block:"start"});history.pushState(null,"",hash)},50)}})}const revealSection=function(entries,observer){const[entry]=entries;if(entry.isIntersecting){entry.target.classList.remove("section--hidden");observer.unobserve(entry.target)}};const sectionObserver=new IntersectionObserver(revealSection,{threshold:.15,rootMargin:"0px 0px -100px 0px"});allSections.forEach(section=>{section.classList.add("section--hidden");sectionObserver.observe(section)});const sectionsToHideButtons=[document.querySelector(".hero"),document.querySelector(".contact-us"),document.querySelector(".footer")];const handleStickyButtons=entries=>{let isInSection=false;entries.forEach(entry=>{if(entry.isIntersecting)isInSection=true});if(isInSection){stickyButtons?.classList.remove("show")}else{stickyButtons?.classList.add("show")}};const buttonObserver=new IntersectionObserver(handleStickyButtons,{threshold:.2});sectionsToHideButtons.forEach(section=>{if(section)buttonObserver.observe(section)});window.addEventListener("load",()=>{document.body.style.overflow="";if(header.classList.contains("nav-open")){header.classList.remove("nav-open")}});const btn=document.querySelector(".btn-back-to-top");if(!btn)return;window.addEventListener("scroll",function(){if(window.scrollY>300){btn.classList.add("show")}else{btn.classList.remove("show")}});btn.addEventListener("click",function(){window.scrollTo({top:0,behavior:"smooth"})})});